(function(){tinymce.create('tinymce.plugins.RSCWPlugin',{init:function(ed,url){ed.addCommand('RSCW_checkSpelling',function(){if(typeof(RapidSpellCheckWeb)!="undefined"){RapidSpellCheckWeb.checkSpelling(true,[ed.id],function(tbElementName){this.tbName=tbElementName;this.ignoreXML=true;this.getText=function(){return document.getElementById(this.tbName).value};this.setText=function(text){document.getElementById(this.tbName).value=text;tinyMCE.get(this.tbName).setContent(text)}})}else{alert("An error accured while seting up Spell Checker!\nSorry for inconvenience.")}});ed.addButton('rapidspellcheck',{title:'Check Spelling',cmd:'RSCW_checkSpelling',image:url+'/img/rapidspellcheck.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('rapidspellcheck',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Spell Check',author:'Dalius.Sidlauskas@exigenservices.com',authorurl:'https://wiki.exigenservices.com/display/IPB/Rapid+Spell+Check+plugin+%28Tiny+MCE%29',infourl:'https://wiki.exigenservices.com/display/IPB/Rapid+Spell+Check+plugin+%28Tiny+MCE%29',version:"1.0"}}});tinymce.PluginManager.add('rapidspellcheck',tinymce.plugins.RSCWPlugin)})();